        }
        .clear-custom-btn {
            padding: 2px 8px;
            font-size: 0.75em;
        }
        .solution-icons {
            margin-top: 6px;
        }
    </style>
</head>
<body>
<div class="custom-container">
    <div class="header">
        <div>
            <h1 class="title">Mastermind Solver</h1>
            <div class="game-info">12 Icons (T1-T6, B1-B6) • $($Config.NumPegs) Pegs • Duplicates $(if ($Config.AllowDuplicates) { 'Allowed' } else { 'Not Allowed' })</div>
        </div>
        <button id="resetBtn" class="btn btn-primary btn-sm reset-btn">Reset</button>
    </div>
    
    <div id="stats-section" class="stats-box" style="display: none;">
        <strong>Possibilities:</strong> <span id="possibilitiesCount">0</span>
        <span style="margin-left: 20px;"><strong>Guess:</strong> <span id="guessNumber">1</span></span>
    </div>
    
    <div id="cur-guess"></div>
    <div id="cur-guess-alert" class="alert alert-danger py-2" style="display: none; font-size: 0.85em;"></div>
    <div id="success" class="alert alert-success py-2" style="display: none;">
        <strong>🎉 Success!</strong> Solved in <span id="totalGuesses"></span> guesses!
    </div>
    
    <table id="old-guesses-table" class="table table-sm table-striped table-hover" style="display: none; font-size: 0.85em;">
        <thead class="thead-light">
            <tr>
                <th>Guess</th>
                <th style="width: 70px;">Exact</th>
                <th style="width: 70px;">Color</th>
                <th style="width: 60px;">Skip</th>
            </tr>
        </thead>
        <tbody id="old-guesses"></tbody>
    </table>
    
    <script id="new-guess-template" type="text/x-handlebars-template">
        <div class="card mb-3">
            <div class="card-body py-3 px-3">
                <h6 class="card-title mb-2" style="color: #007bff;">Next Guess</h6>
                <div class="mb-2">
                    <label class="compact-label">Suggested:</label>
                    <div class="guess-display">{{{guessHtml}}}</div>
                </div>
                <div class="mb-2">
                    <label class="compact-label">Or click icons to build your own:</label>
                    <div class="icon-picker">
                        <div class="icon-picker-row">
                            <button type="button" class="icon-pick-btn" data-icon="T1"><img src="" data-icon-src="T1" class="icon-pick-img" alt="T1"></button>
                            <button type="button" class="icon-pick-btn" data-icon="T2"><img src="" data-icon-src="T2" class="icon-pick-img" alt="T2"></button>
                            <button type="button" class="icon-pick-btn" data-icon="T3"><img src="" data-icon-src="T3" class="icon-pick-img" alt="T3"></button>
                            <button type="button" class="icon-pick-btn" data-icon="T4"><img src="" data-icon-src="T4" class="icon-pick-img" alt="T4"></button>
                            <button type="button" class="icon-pick-btn" data-icon="T5"><img src="" data-icon-src="T5" class="icon-pick-img" alt="T5"></button>
                            <button type="button" class="icon-pick-btn" data-icon="T6"><img src="" data-icon-src="T6" class="icon-pick-img" alt="T6"></button>
                        </div>
                        <div class="icon-picker-row">
                            <button type="button" class="icon-pick-btn" data-icon="B1"><img src="" data-icon-src="B1" class="icon-pick-img" alt="B1"></button>
                            <button type="button" class="icon-pick-btn" data-icon="B2"><img src="" data-icon-src="B2" class="icon-pick-img" alt="B2"></button>
                            <button type="button" class="icon-pick-btn" data-icon="B3"><img src="" data-icon-src="B3" class="icon-pick-img" alt="B3"></button>
                            <button type="button" class="icon-pick-btn" data-icon="B4"><img src="" data-icon-src="B4" class="icon-pick-img" alt="B4"></button>
                            <button type="button" class="icon-pick-btn" data-icon="B5"><img src="" data-icon-src="B5" class="icon-pick-img" alt="B5"></button>
                            <button type="button" class="icon-pick-btn" data-icon="B6"><img src="" data-icon-src="B6" class="icon-pick-img" alt="B6"></button>
                        </div>
                    </div>
                    <div class="custom-guess-display mt-1">
                        <div class="custom-guess-icons"></div>
                        <button type="button" class="btn btn-outline-secondary btn-sm clear-custom-btn" style="display:none;">Clear</button>
                    </div>
                    <input type="hidden" class="custom-guess-input" value="">
                </div>
                <div class="row">
                    <div class="col-6">
                        <label class="compact-label">Exact matches:</label>
                        <input type="number" min="0" max="$($Config.NumPegs)" value="0" class="form-control form-control-sm bothCorrect">
                    </div>
                    <div class="col-6">
                        <label class="compact-label">Color only:</label>
                        <input type="number" min="0" max="$($Config.NumPegs)" value="0" class="form-control form-control-sm colorCorrect">
                    </div>
                </div>
                <button class="btn btn-success btn-sm btn-block mt-2 submit-combined">Submit Guess</button>
            </div>
        </div>
    </script>

    <script id="old-guess-template" type="text/x-handlebars-template">
        <tr class="evidence">
            <td>{{{guessHtml}}}</td>
            <td><input type="number" min="0" max="$($Config.NumPegs)" value="{{bothCorrect}}" class="form-control form-control-sm bothCorrect-edit" style="width: 50px;"></td>
            <td><input type="number" min="0" max="$($Config.NumPegs)" value="{{colorCorrect}}" class="form-control form-control-sm colorCorrect-edit" style="width: 50px;"></td>
            <td class="text-center"><input type="checkbox" class="form-check-input ignore-row"></td>
        </tr>
    </script>
</div>
<script src="https://code.jquery.com/jquery-3.3.1.slim.min.js"></script>
<script src="https://stackpath.bootstrapcdn.com/bootstrap/4.3.1/js/bootstrap.min.js"></script>
<script src="https://cdnjs.cloudflare.com/ajax/libs/handlebars.js/4.1.2/handlebars.min.js"></script>
"@

# ============================================================================
# SECTION 4D: JAVASCRIPT LOGIC
# ============================================================================
$JavaScriptCode = @"
<script>
`$(function() {
    'use strict';
    
    // ========================================================================
    // ICON DATA - Injected from PowerShell
    // ========================================================================
    $IconDataJS
    
    // ========================================================================
    // GAME CONFIGURATION - Injected from PowerShell
    // ========================================================================
$ConfigJS
    
    // ========================================================================
    // GLOBAL STATE
    // ========================================================================
    let globalGameState = null;
    let guessCount = 0;
    
    // ========================================================================
    // JQUERY ELEMENT REFERENCES
    // ========================================================================
    const `$playAreaCurGuess = `$('#cur-guess');
    const `$playAreaOldGuesses = `$('#old-guesses');
    const `$success = `$('#success');
    const `$resetBtn = `$('#resetBtn');
    const `$curGuessAlert = `$('#cur-guess-alert');
    const `$oldGuessesTable = `$('#old-guesses-table');
    const `$statsSection = `$('#stats-section');
    const `$possibilitiesCount = `$('#possibilitiesCount');
    const `$guessNumber = `$('#guessNumber');
    
    const newGuessTemplate = Handlebars.compile(`$('#new-guess-template').html());
    const oldGuessTemplate = Handlebars.compile(`$('#old-guess-template').html());
    
    // ========================================================================
    // UTILITY FUNCTIONS
    // ========================================================================
    
    function toIconHtml(color) {
        return '<img src="' + iconData[color] + '" class="icon-display" alt="' + color + '">';
    }
    
    function toGuessHtml(guessArr) {
        return guessArr.map(toIconHtml).join('');
    }
    
    function updatePossibilitiesCount(count) {
        `$possibilitiesCount.text(count);
    }
    
    // ========================================================================
    // MASTERMIND ALGORITHM - Core Logic
    // ========================================================================
    
    function generateAllPossibleGuessesWithDups(n, colors) {
        let guesses = [];
        function recurse(current) {
            if (current.length === n) {
                guesses.push([...current]);
                return;
            }
            for (let color of colors) {
                current.push(color);
                recurse(current);
                current.pop();
            }
        }
        recurse([]);
        return guesses;
    }
    
    function scoreGuess(secret, guess) {
        let secretCopy = [...secret];
        let guessCopy = [...guess];
        let exactMatches = 0;
        
        // First pass: count exact matches
        for (let i = 0; i < secretCopy.length; i++) {
            if (secretCopy[i] === guessCopy[i]) {
                exactMatches++;
                secretCopy[i] = null;
                guessCopy[i] = null;
            }
        }
        
        // Second pass: count color-only matches
        let colorOnlyMatches = 0;
        for (let i = 0; i < guessCopy.length; i++) {
            if (guessCopy[i] !== null) {
                let idx = secretCopy.indexOf(guessCopy[i]);
                if (idx !== -1) {
                    colorOnlyMatches++;
                    secretCopy[idx] = null;
                }
            }
        }
        
        return { exact: exactMatches, color: colorOnlyMatches };
    }
    
    function filterPossibleGuesses(possibles, guess, exact, color) {
        return possibles.filter(candidate => {
            let result = scoreGuess(candidate, guess);
            return result.exact === exact && result.color === color;
        });
    }
    
    function pickBestGuess(possibles) {
        if (possibles.length === 0) return null;
        if (possibles.length === 1) return possibles[0];
        
        let bestGuess = possibles[0];
        let minMaxPartition = Infinity;
        
        // Limit the candidate pool for performance if too large
        let candidatePool = possibles.length > MAX_CANDIDATES 
            ? possibles.slice(0, MAX_CANDIDATES) 
            : possibles;
        
        for (let candidate of candidatePool) {
            let partitions = {};
            
            for (let possible of possibles) {
                let result = scoreGuess(possible, candidate);
                let key = result.exact + ',' + result.color;
                partitions[key] = (partitions[key] || 0) + 1;
            }
            
            let maxPartition = Math.max(...Object.values(partitions));
            
            if (maxPartition < minMaxPartition) {
                minMaxPartition = maxPartition;
                bestGuess = candidate;
            }
        }
        
        return bestGuess;
    }
    
    // ========================================================================
    // UI UPDATE FUNCTIONS
    // ========================================================================
    
    /**
     * Main UI update function - RECALCULATES FROM SCRATCH EVERY TIME
     */
    function updateUiWithAGuess() {
        // 1. Reset to ALL permutations
        let currentPossibles = globalGameState.allPermutations;
        
        // 2. Collect all evidence from table and filter sequentially
        let evidences = [];
        
        `$playAreaOldGuesses.find('.evidence').each(function() {
            let `$row = `$(this);
            
            // Skip rows marked as "ignore" (checkbox)
            if (`$row.find('.ignore-row').is(':checked')) return;
            
            // Extract the guess from the HTML
            let guessHtml = `$row.find('td:first').html();
            let guessArray = [];
            `$(guessHtml).each(function() {
                guessArray.push(`$(this).attr('alt'));
            });
            
            // Get the feedback values
            let exact = parseInt(`$row.find('.bothCorrect-edit').val()) || 0;
            let color = parseInt(`$row.find('.colorCorrect-edit').val()) || 0;
            
            // Apply filter immediately
            currentPossibles = filterPossibleGuesses(currentPossibles, guessArray, exact, color);
            
            // Store for opening sequence logic
            evidences.push({ guess: guessArray, exact, color });
        });
        
        updatePossibilitiesCount(currentPossibles.length);
        
        // 3. Handle specific states
        
        // State: Impossible (User likely made a typo)
        if (currentPossibles.length === 0) {
            `$curGuessAlert.html('<strong>Error:</strong> No solutions match this criteria. Check your previous inputs or use the "Skip" checkbox on incorrect rows.');
            `$curGuessAlert.show();
            `$playAreaCurGuess.empty(); // Remove the submit button so they don't dig deeper
            return;
        }
        
        `$curGuessAlert.hide();
        
        // State: Solved (Only 1 remains)
        if (currentPossibles.length === 1) {
            let solution = currentPossibles[0];
            // Check if we actually found it (last evidence was 4 exact) to show "Success" vs "Here is the answer"
            let lastEv = evidences[evidences.length - 1];
            if (lastEv && lastEv.exact === NUM_PEGS) {
                `$success.find('#totalGuesses').text(guessCount);
                `$success.show();
                `$playAreaCurGuess.html('<div class="alert alert-info py-2"><strong>Game Over!</strong> Click Reset to play again.</div>');
            } else {
                 `$playAreaCurGuess.html('<div class="alert alert-info py-2"><strong>Solution Found:</strong><div class="solution-icons">' + toGuessHtml(solution) + '</div>Enter this as your next guess to confirm.</div>' +
                                          '<div class="card mb-3"><div class="card-body"><button class="btn btn-success btn-sm btn-block submit-combined">Fill Solution</button></div></div>');
                 `$playAreaCurGuess.find('.card').data('suggestedGuess', solution);
            }
            return;
        }
        
        `$success.hide();
        
        // 4. Determine Next Guess
        let nextGuess = null;
        
        // A. Check Opening Sequence
        if (OPENING_SEQUENCE && guessCount < OPENING_SEQUENCE.length) {
            // Only use opening sequence if we haven't found all pegs (exact matches) yet
            // If the user got 3/4 exacts on turn 1, we shouldn't blindly fire the specific opening sequence, 
            // but usually opening sequences are designed to be played regardless. 
            // Standard behavior: Play opening sequence unless previous feedback makes it impossible? 
            // NebuPookins behavior: Fixed opening sequence.
            nextGuess = OPENING_SEQUENCE[guessCount];
            
            // Important: If the hardcoded opening guess is now impossible (contradicts evidence), switch to algorithm
            let isOpeningValid = true;
            let testSet = currentPossibles;
            // Quick check if nextGuess exists in currentPossibles (using JSON stringify for array comparison)
            // This can be slow for large sets, so we'll trust the algorithm fallback if the user wants strictly optimal play.
            // For now, let's stick to the requested opening sequence.
        }
        
        // B. Algorithm (Minimax)
        if (!nextGuess) {
            nextGuess = pickBestGuess(currentPossibles);
        }
        
        if (!nextGuess) {
             `$playAreaCurGuess.html('<div class="alert alert-warning py-2">Calculation error.</div>');
             return;
        }
        
        // 5. Render Next Guess Card
        let html = newGuessTemplate({
            guessHtml: toGuessHtml(nextGuess)
        });
        `$playAreaCurGuess.html(html);
        `$playAreaCurGuess.find('.card').data('suggestedGuess', nextGuess);
    }
    
    function submitGuess(guessArray) {
        let exact = parseInt(`$playAreaCurGuess.find('.bothCorrect').val()) || 0;
        let color = parseInt(`$playAreaCurGuess.find('.colorCorrect').val()) || 0;
        
        if (exact + color > NUM_PEGS) {
            `$curGuessAlert.text('Exact + Color cannot exceed ' + NUM_PEGS);
            `$curGuessAlert.show();
            return;
        }
        
        guessCount++;
        `$guessNumber.text(guessCount + 1);
        
        let html = oldGuessTemplate({
            guessHtml: toGuessHtml(guessArray),
            bothCorrect: exact,
            colorCorrect: color
        });
        `$playAreaOldGuesses.append(html);
        `$oldGuessesTable.show();
        
        updateUiWithAGuess();
    }
    
    // ========================================================================
    // GAME INITIALIZATION
    // ========================================================================
    
    function startGame() {
        `$playAreaOldGuesses.empty();
        `$playAreaCurGuess.empty();
        `$curGuessAlert.hide();
        `$success.hide();
        `$oldGuessesTable.hide();
        guessCount = 0;
        
        // Generate immutable master list
        let allPermutations = generateAllPossibleGuessesWithDups(NUM_PEGS, allColors);
        
        globalGameState = {
            numPegs: NUM_PEGS,
            colors: allColors,
            allowDups: ALLOW_DUPS,
            allPermutations: allPermutations // Keep master copy
        };
        
        `$statsSection.show();
        `$possibilitiesCount.text(allPermutations.length);
        `$guessNumber.text('1');
        
        updateUiWithAGuess();
    }
    
    // ========================================================================
    // EVENT HANDLERS
    // ========================================================================
    
    `$resetBtn.on('click', startGame);
    
    `$playAreaCurGuess.on('click', '.submit-combined', function() {
        `$curGuessAlert.hide();
        let customGuessText = `$playAreaCurGuess.find('.custom-guess-input').val() || ''; 
        customGuessText = customGuessText.trim();
        
        if (!customGuessText) {
            let suggestedGuess = `$playAreaCurGuess.find('.card').data('suggestedGuess');
            submitGuess(suggestedGuess);
            return;
        }
        
        let customGuessColors = customGuessText.split(/[,\s]+/).map(s => s.trim()).filter(s => s);
        
        if (customGuessColors.length !== NUM_PEGS) {
            `$curGuessAlert.text("Your guess must have exactly " + NUM_PEGS + " icons.");
            `$curGuessAlert.show();
            return;
        }
        
        let normalizedGuess = [];
        for (let color of customGuessColors) {
            let matchedColor = allColors.find(c => c.toUpperCase() === color.toUpperCase());
            if (!matchedColor) {
                `$curGuessAlert.text("Invalid icon: '" + color + "'. Valid: T1-T6, B1-B6");
                `$curGuessAlert.show();
                return;
            }
            normalizedGuess.push(matchedColor);
        }
        
        submitGuess(normalizedGuess);
    });
    
    // THE CRITICAL RESTORE FUNCTIONALITY
    // Recalculates everything when any input in history changes
    `$playAreaOldGuesses.on('change', 'input[type="number"], input[type="checkbox"]', function() {
        updateUiWithAGuess();
    });
    
    // ========================================================================
    // ICON PICKER FUNCTIONALITY
    // ========================================================================
    
    // Initialize icon picker images when card is rendered
    function initIconPicker() {
        `$playAreaCurGuess.find('.icon-pick-btn').each(function() {
            let icon = `$(this).data('icon');
            `$(this).find('img').attr('src', iconData[icon]);
        });
    }
    
    // Watch for new card being added and initialize icon picker
    const observer = new MutationObserver(function(mutations) {
        mutations.forEach(function(mutation) {
            if (mutation.addedNodes.length) {
                initIconPicker();
            }
        });
    });
    observer.observe(document.getElementById('cur-guess'), { childList: true });
    
    // Handle icon button clicks
    `$playAreaCurGuess.on('click', '.icon-pick-btn', function() {
        let icon = `$(this).data('icon');
        let `$input = `$playAreaCurGuess.find('.custom-guess-input');
        let `$iconsDisplay = `$playAreaCurGuess.find('.custom-guess-icons');
        let `$clearBtn = `$playAreaCurGuess.find('.clear-custom-btn');
        
        let currentVal = `$input.val();
        let icons = currentVal ? currentVal.split(',') : [];
        
        // Only add if we haven't reached max pegs
        if (icons.length < NUM_PEGS) {
            icons.push(icon);
            `$input.val(icons.join(','));
            
            // Update visual display
            `$iconsDisplay.html(icons.map(toIconHtml).join(''));
            `$clearBtn.show();
        }
    });
    
    // Handle clear button
    `$playAreaCurGuess.on('click', '.clear-custom-btn', function() {
        `$playAreaCurGuess.find('.custom-guess-input').val('');
        `$playAreaCurGuess.find('.custom-guess-icons').empty();
        `$(this).hide();
    });
    
    // Auto-start
    startGame();
});
</script>
</body>
</html>
"@

# ============================================================================
# SECTION 4E: COMBINE ALL HTML SECTIONS
# ============================================================================
$HtmlContent = $HtmlStructure + $CssStyles + $JavaScriptCode

# ============================================================================
# SECTION 5: WRITE HTML TO TEMP FILE
# ============================================================================
try {
    $TempPath = Join-Path $env:TEMP "MastermindSolver.html"
    $HtmlContent | Out-File -FilePath $TempPath -Encoding utf8 -ErrorAction Stop
}
catch {
    exit 1
}

# ============================================================================
# SECTION 6: BROWSER/WINDOW MANAGEMENT FUNCTION
# ============================================================================

function Start-MastermindWindow {
    <#
    .SYNOPSIS
        Launches and configures the Mastermind Solver window
    
    .PARAMETER HtmlPath
        Path to the HTML file to display
    
    .PARAMETER Config
        Configuration hashtable with window settings
    #>
    param(
        [string]$HtmlPath,
        [hashtable]$Config
    )
    
    # Add Win32 API for window manipulation (if not already loaded)
    if (-not ([System.Management.Automation.PSTypeName]'Win32').Type) {
        Add-Type @"
using System;
using System.Runtime.InteropServices;
public class Win32 {
    [DllImport("user32.dll")]
    public static extern IntPtr FindWindow(string lpClassName, string lpWindowName);
    [DllImport("user32.dll")]
    public static extern bool SetWindowPos(IntPtr hWnd, IntPtr hWndInsertAfter, int X, int Y, int cx, int cy, uint uFlags);
    [DllImport("user32.dll")]
    public static extern int SetWindowLong(IntPtr hWnd, int nIndex, int dwNewLong);
    [DllImport("user32.dll")]
    public static extern int GetWindowLong(IntPtr hWnd, int nIndex);
    [DllImport("user32.dll")]
    public static extern bool SetLayeredWindowAttributes(IntPtr hwnd, uint crKey, byte bAlpha, uint dwFlags);
    [DllImport("user32.dll")]
    [return: MarshalAs(UnmanagedType.Bool)]
    public static extern bool GetWindowRect(IntPtr hWnd, out RECT lpRect);
    [DllImport("user32.dll")]
    public static extern IntPtr GetForegroundWindow();
    [StructLayout(LayoutKind.Sequential)]
    public struct RECT {
        public int Left; public int Top; public int Right; public int Bottom;
    }
    public static readonly IntPtr HWND_TOPMOST = new IntPtr(-1);
    public const uint SWP_NOMOVE = 0x0002;
    public const uint SWP_NOSIZE = 0x0001;
    public const int GWL_EXSTYLE = -20;
    public const int WS_EX_LAYERED = 0x80000;
    public const int LWA_ALPHA = 0x2;
}
"@
    }
    
    # Launch browser
    try {
        $process = Start-Process $Config.Browser -ArgumentList "--app=$HtmlPath", "--new-window" -PassThru -ErrorAction Stop
    }
    catch {
        return $false
    }
    
    # Wait for window to appear
    Start-Sleep -Milliseconds 1500
    
    # Find the window
    $windowTitle = "*Mastermind Solver*"
    $maxAttempts = 10
    $attempt = 0
    $hwnd = [IntPtr]::Zero
    
    while ($attempt -lt $maxAttempts) {
        $edgeProcesses = Get-Process msedge -ErrorAction SilentlyContinue
        foreach ($proc in $edgeProcesses) {
            if ($proc.MainWindowTitle -like $windowTitle) {
                $hwnd = $proc.MainWindowHandle
                break
            }
        }
        if ($hwnd -ne [IntPtr]::Zero) { break }
        Start-Sleep -Milliseconds 300
        $attempt++
    }
    
    if ($hwnd -eq [IntPtr]::Zero) {
        return $true  # Still return true since browser launched
    }
    
    # Configure window
    # Enable layered window for transparency
    $currentStyle = [Win32]::GetWindowLong($hwnd, [Win32]::GWL_EXSTYLE)
    $newStyle = $currentStyle -bor [Win32]::WS_EX_LAYERED
    [Win32]::SetWindowLong($hwnd, [Win32]::GWL_EXSTYLE, $newStyle) | Out-Null
    
    # Set window size and position
    $rect = New-Object Win32+RECT
    [Win32]::GetWindowRect($hwnd, [ref]$rect) | Out-Null
    
    if ($Config.AlwaysOnTop) {
        [Win32]::SetWindowPos($hwnd, [Win32]::HWND_TOPMOST, 
                             $rect.Left, $rect.Top, 
                             $Config.WindowWidth, $Config.WindowHeight, 
                             0) | Out-Null
    } else {
        [Win32]::SetWindowPos($hwnd, [IntPtr]::Zero, 
                             $rect.Left, $rect.Top, 
                             $Config.WindowWidth, $Config.WindowHeight, 
                             0) | Out-Null
    }
    
    # Transparency monitoring loop
    $lastActiveState = $null
    
    try {
        while ($true) {
            $proc = Get-Process msedge -ErrorAction SilentlyContinue | Where-Object { $_.MainWindowHandle -eq $hwnd }
            if (-not $proc) { 
                break 
            }
            
            $foregroundWindow = [Win32]::GetForegroundWindow()
            $isActive = ($foregroundWindow -eq $hwnd)
            
            # Update transparency only when state changes
            if ($isActive -ne $lastActiveState) {
                if ($isActive) {
                    [Win32]::SetLayeredWindowAttributes($hwnd, 0, $Config.WindowAlphaActive, [Win32]::LWA_ALPHA) | Out-Null
                } else {
                    [Win32]::SetLayeredWindowAttributes($hwnd, 0, $Config.WindowAlpha, [Win32]::LWA_ALPHA) | Out-Null
                }
                $lastActiveState = $isActive
            }
            
            Start-Sleep -Milliseconds 200
        }
    } 
    catch { }
    
    return $true
}

# ============================================================================
# SECTION 7: MAIN EXECUTION
# ============================================================================
try {
    $result = Start-MastermindWindow -HtmlPath $TempPath -Config $Config
}
catch {
    exit 1
}
